    public function laborers(Request $request)
    {
        $search = $request->name;

        $laborers = Laborer::leftJoin('agencies','laborers.agency_id','agencies.id')
        ->where(function($q) use($search) {
            $q->where('name', 'like', '%'.$search.'%');
            $q->orWhere('agencies.agency_name', 'like', '%'.$search.'%');
        })
        ->orderby('laborers.name','asc')
        ->select('laborers.id','laborers.name','laborers.usruid','laborers.agency_id','laborers.active','agencies.agency_name as agency_name')
        ->paginate(10);
        $active_count = Laborer::where('active','AC')->count();
        $inactive_count = Laborer::where('active','IN')->count();
        $agencies = Agency::orderBy('agency_name','asc')->get();
       
        return view('laborers',array
        (
            'laborers' => $laborers,
            'active_count' => $active_count,
            'inactive_count' => $inactive_count,
            'agencies' => $agencies,
        )); 
    }
    public function new_laborer(Request $request)
    {
        $this->validate(request(),[
            'name' => 'required|string|max:255|regex:/^[\pL\s\-]+$/u',
            'user_id' => 'required|unique:laborers,usruid|',
            ]    
        ); 
        $data = new Laborer;
        $data->id = $request->input('user_id');
        $data->usruid = $request->input('user_id');
        $data->name = $request->input('name');
        $data->agency_id = $request->input('agency');
        $data->active = $request->input('status');
         $image = "data:image/png;base64,";
        $client1 = new Client([
            'base_uri' => 'http://172.17.2.24:8795/v2/',
            'cookies' => true,
            ]);
            
        $client1->request('POST', 'login',[
            'json' => [
                'name' => 'IC TEST API',
                'password' => 'iamprogrammer',
                'user_id' => 'dev'
                ]
                ]);
             
        try 
        {
            $request1 = $client1->request('GET', 'users/'.$request->user_id.'/photo');
            if($request1->getStatusCode() == 200)
            {
                $data->image = "data:image/png;base64,".json_decode((string) $request1->getBody(), true);
                // dd( $data->image);
            }
        }
        catch (\GuzzleHttp\Exception\ClientException $e) 
        {
            $data->image = $image;
        }
        $client1->request('GET', 'logout');  
        $data->save();
        $request->session()->flash('status',$data->name . ' New Laborer Encoded');
        return back();
    }
    public function save_edit_laborer(Request $request, $id)
    {
        $this->validate(request(),[
            'name' => 'required|string|max:255||regex:/^[\pL\s\-]+$/u',
            ]    
        ); 
        $data =  Laborer::find($id);
        $data->name = $request->input('name');
        $data->agency_id = $request->input('agency');
        $data->active = $request->input('status');
        $data->save();
        $request->session()->flash('status',$data->name . ' Successfully Changed!');
        return back();
    }
