    public function view_laborers_ot(Request $request)
    {
        $file_ot = FileOt::get();
        $company_allowed = $file_ot->pluck('company_id')->toArray();
        $department_allowed = $file_ot->pluck('department_id')->toArray();
        $work_allowed = $file_ot->pluck('work_id')->toArray();
        $from_date = $request->from;


        $laborers = Schedule::whereDate('date','=',$from_date)
        ->leftJoin('laborers','schedules.laborer_id','=','laborers.id')
        ->whereIn('schedules.work_id',json_decode(auth()->user()->work))
        ->whereIn('schedules.company_id',json_decode(auth()->user()->company))
        ->whereIn('schedules.location_id',json_decode(auth()->user()->department))
        ->whereIn('schedules.work_id',$work_allowed)
        ->whereIn('schedules.company_id',$company_allowed)
        ->whereIn('schedules.location_id', $department_allowed)
        ->whereIn('laborers.agency_id',json_decode(auth()->user()->agency))
        ->select('laborers.id','laborers.name')
        ->orderBy('laborers.name','asc')    
        ->groupBy('laborers.id','laborers.name')
        ->get(['laborers.id','laborers.name']);

        return $laborers;
    }
