    public function printID($laborer_id)
    {

        $laborer = Laborer::with('agency')->where('id',$laborer_id)->first();

        if($laborer){
            
            //Save Logs 

            $print_id_logs_data = [];
            $print_id_logs_data['laborer_id'] = $laborer->id;
            $print_id_logs_data['user_id'] = Auth::user()->id;
            $print_id_logs_data['remarks'] = 'Print on ' . date('Y-m-d') . ' at ' . date('h:m:s');
            
            PrintIdLog::create($print_id_logs_data);

            //Print ID 

            Fpdf::AddPage("P", [53.98,85.60]);
            Fpdf::SetMargins(0,0,0,0);
            Fpdf::SetAutoPageBreak(false);
            
            Fpdf::SetFont('Courier', 'B', 18);

            if(!empty($laborer->image)){
                $destinationPath = public_path() . '/id_image/temp_laborer_image/';
                if(!File::isDirectory($destinationPath)){
                    File::makeDirectory($destinationPath, 0777, true, true);
                }
                $img = Image::make(file_get_contents($laborer->image));
                $img->resize(500, 500, function ($constraint) {
                    $constraint->aspectRatio();
                })->save($destinationPath.'/'. $laborer->id . '.png');
                if (file_exists( public_path() . '/id_image/temp_laborer_image/' . $laborer->id . '.png')){
                    $image = public_path() . '/id_image/temp_laborer_image/' . $laborer->id . '.png';
                    Fpdf::Image($image, 6, 4, 41, 41);
                }           
            }

            //Background

            // if(!empty($laborer->agency->background_image)){
            //     $destinationPath = public_path() . '/id_image/temp_agency_image/';
            //     if(!File::isDirectory($destinationPath)){
            //         File::makeDirectory($destinationPath, 0777, true, true);
            //     }
            //     $img = Image::make(file_get_contents($laborer->agency->background_image));
            //     $img->resize(500, 500, function ($constraint) {
            //         $constraint->aspectRatio();
            //     })->save($destinationPath.'/'. $laborer->agency->id . '.png');
            //     if (file_exists( public_path() . '/id_image/temp_agency_image/' . $laborer->agency->id . '.png')){
                    // $image = public_path() . '/id_image/temp_agency_image/' . $laborer->agency->id . '.png';
                    // Fpdf::Image( $image, 0, 0, 53.98, 85.60);
                    $image = public_path() . '/id_image/temp_agency_image/1.png';
                    Fpdf::Image( $image, 0, 0, 53.98, 85.60);
            //     }           
            // }

            Fpdf::SetFont('Arial', 'B', 12);
            Fpdf::SetXY(0,48);
            if($laborer->agency){
                Fpdf::MultiCell(53.98,4, $laborer->agency->agency_name . ' - ' .  $laborer->id ,0,'C');
            }

            Fpdf::SetFont('Arial', 'B', 16);
            Fpdf::SetXY(0,65);
            if($laborer->name){
                Fpdf::MultiCell(53.98,5, $laborer->name ,0,'C');
            }

            Fpdf::Output($laborer->id . ".pdf", 'I');
            exit();
        }
    }
